mapper.select:
		SELECT * 
 	FROM (
   	  SELECT ROWNUM RNUM, j.*
   	  FROM (
     	    SELECT post.*, NVL(cnt, 0) AS comment_count
     	    FROM post
     	    LEFT OUTER JOIN (
     	        SELECT post_id, COUNT(*) cnt
     	        FROM comments
     	        GROUP BY post_id
    	     ) sub_C
    	     ON post.post_id = sub_C.post_id
         	WHERE post.board_id = #{board_id}
         	ORDER BY post.post_id DESC
     	) j 
     	WHERE ROWNUM <= #{end} 
 	) 
 	WHERE RNUM >= #{start} AND RNUM <= #{end}
